import React, {useState} from 'react';
import './App.css';
import Button from './Button'

function App() {

const [hex, setHex] = useState('');

  return (
    <div className="App" style={{backgroundColor: `#${hex}`}}>
      <h2>{'HEX RANDOM'}</h2>
        <Button setHex={setHex} />
    </div>
  );
}

export default App;
