import React from 'react'

function Button({handleChange}) {

const abc = 'AaBbCcDdEeFfGgHhIiJjKkLlMmNnOoPpQqRrSsTtUuVvWwXxYyZz'
const num = '1234567890'

const hexFunction = () => {
  let hexColor = ''
  let abcLength = abc.length

  for (let i = 0; i < 7; i++) {
    hexColor += abc.charAt(Math.floor(Math.random() * abcLength))
  }
  console.log(hexColor)
  handleChange(hexColor)
}

  return (
    <>
    <button onClick={hexFunction}>Go Hex!</button>
    </>
  )
}

export default Button
