import React from 'react'

function Button({handleChange}) {

const abc = 'AaBbCcDdEeFfGgHhIiJjKkLlMmNnOoPpQqRrSsTtUuVvWwXxYyZz'
const num = '1234567890'

const hexFunction = () => {
  let hexColor = ''
  let abcLength = abc.length
  let totalIndexRandom = Math.floor(Math.random() * 6)

  for (let i = 0; i < 6; i++) {
    hexColor += abc.charAt(Math.floor(Math.random() * abcLength))
  }
  console.log('Total index', totalIndexRandom)
  return hexColor
}

  return (
    <>
    <button onClick={() => handleChange(hexFunction)}>Go Hex!</button>
    </>
  )
}

export default Button
