import React from 'react'

function Button({setHex}) {

const abc = 'AaBbCcDdEeFfGgHhIiJjKkLlMmNnOoPpQqRrSsTtUuVvWwXxYyZz'
const num = '1234567890'

const hexFunction = (e) => {
  e.preventDefault()
  let hexColor = ''
  let abcLength = abc.length

  for (let i = 0; i < 7; i++) {
    hexColor += abc.charAt(Math.floor(Math.random() * abcLength))
  }
  setHex(hexColor)
}

  return (
    <>
    <button onClick={(e) => console.log(e)}>Go Hex!</button>
    </>
  )
}

export default Button
