import React, {useState} from 'react';
import './App.css';
import Button from './Button'

function App() {

const [hex, setHex] = useState('');

function handleChange (hexValue) {
  setHex(hexValue)
}

  return (
    <div className="App" style={{backgroundColor: `#${hex}`}}>
      <h2>{'HEX RANDOM'}</h2>
        <Button handleChange={handleChange} />
    </div>
  );
}

export default App;
